import React, { useState, useRef } from 'react';
import './ImageUpload.css'; // Assuming ImageUpload.css exists here too

// Note: Converted props handling and event types from TypeScript to JavaScript
function ImageUpload({ onImageUpload }) {
  const [dragActive, setDragActive] = useState(false);
  const [previewUrl, setPreviewUrl] = useState(null);
  const inputRef = useRef(null);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleChange = (e) => {
    e.preventDefault();
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  // Clear previous preview if user uploads a new file
  const handleFile = (file) => {
    console.log("ImageUpload.js: handleFile called with file:", file);
    // Revoke previous object URL to prevent memory leaks
    if (previewUrl) {
      URL.revokeObjectURL(previewUrl);
    }
    setPreviewUrl(URL.createObjectURL(file));
    onImageUpload(file);
    console.log("ImageUpload.js: onImageUpload prop called.");
  };

  const onButtonClick = () => {
    // Trigger the hidden file input click
    inputRef.current?.click();
  };

  return (
    <div className="upload-container">
      <form
        className={`upload-form ${dragActive ? "drag-active" : ""}`}
        onDragEnter={handleDrag}
        onSubmit={(e) => e.preventDefault()} // Prevent default form submission
      >
        {/* Hidden file input */}
        <input
          ref={inputRef}
          type="file"
          accept="image/*" // Accept only image files
          onChange={handleChange}
          style={{ display: "none" }} // Keep it hidden
        />
        {/* Drag and drop area / button trigger area */}
        <div
          className="upload-area"
          onClick={onButtonClick} // Allow clicking the area to trigger upload
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
        >
          {previewUrl ? (
            <img src={previewUrl} alt="Preview" className="preview-image" />
          ) : (
            <>
              <p>Drag and drop your image here or</p>
              {/* Make the button type="button" to prevent form submission */}
              <button type="button" className="upload-button" onClick={onButtonClick}>
                Upload an image
              </button>
            </>
          )}
        </div>
      </form>
    </div>
  );
}

export default ImageUpload;
