import React, { useState } from 'react';
// Assuming ImageUpload.js exists in ./components/
import ImageUpload from './components/ImageUpload';
import './App.css';

function App() {
  // State for API communication
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState(null); // Stores { numberPlate: '...', confidence: ... }
  const [error, setError] = useState(null);   // Stores error messages

  const handleImageUpload = async (file) => {
    if (!file) return;

    setIsLoading(true);
    setError(null);
    setResult(null);

    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await fetch('http://localhost:5000/detect', {
        method: 'POST',
        body: formData,
      });

      // Check response status carefully
      if (response.ok) { // Status 200-299
        const data = await response.json();
        if (data.numberPlate) {
           setResult(data);
        } else {
          // Handle cases where 200 OK but no plate data (shouldn't happen with current backend)
          setError("Received success status but no plate data.");
        }
      } else {
        // Handle non-OK statuses (like 404, 500)
        const errorData = await response.json().catch(() => ({})); // Try parsing error JSON, default to empty object
        const errorMessage = errorData.error || `Request failed with status: ${response.status}`;

        if (response.status === 404) {
          // Specifically handle the "No valid plate detected" case
          setError(errorMessage); // Use the error message from the backend
        } else {
          // Handle other errors (e.g., 500 Internal Server Error)
          setError(`Server error: ${errorMessage}`);
        }
      }

    } catch (err) {
      // Handle network errors or other exceptions during fetch/processing
      console.error("Fetch error:", err);
      setError(err instanceof Error ? err.message : 'An unexpected error occurred during fetch.');
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <div className="app"> {/* Use class from App.css */}
      <header className="app-header">
        <h1>Automatic Number Plate Detection</h1>
        <p className="subtitle">Upload an image to detect license plates</p>
      </header>

      <main className="app-main">
        {/* Use the ImageUpload component */}
        <ImageUpload onImageUpload={handleImageUpload} />

        <div className="results-container">
          {isLoading && (
            <div className="loading">
              <div className="spinner"></div> {/* Use spinner class from App.css */}
              <p>Processing image...</p>
            </div>
          )}

          {/* Display error message */}
          {error && (
            <div className="error"> {/* Use error class from App.css */}
              <p>Error: {error}</p>
            </div>
          )}

          {/* Display result */}
          {result && (
            <div className="result"> {/* Use result class from App.css */}
              <h2>Detection Results</h2>
              <div className="result-card">
                <p className="number-plate">{result.numberPlate}</p>
                <p className="confidence">
                  Confidence: {(result.confidence * 100).toFixed(2)}%
                </p>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
